import React from "react";
import { connect } from "react-redux"
import Textfield from "../Textfield/Textfield";
import Todowrapper from "../Todowrapper/Todowrapper";
import { postTodo } from "../../actions"
import "./Box.css";

class Box extends React.Component {

    constructor(props) {
        super(props );
        this.textRef = React.createRef();
    }
    componentDidMount(){
        console.log(this.textRef);
    }

    onButtonClick = () => {
        this.props.postTodo({todo : this.props.inputValue})
    }

  render() {
    return (
      <div className="box">
        <Todowrapper />
        <Textfield />
        <button className="btn" onClick={() => {this.onButtonClick()}}>+</button>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
    return {
        inputValue : state.inputValue
    }
}

export default connect(mapStateToProps, { postTodo })(Box);
