import "./Todoitem.css";

class Todoitem extends React.Component {
    constructor(props) {
        super(props);

    }


  onCheckboxClick = () => {
    if (this.checkBoxRef.checked()) {
        console.log(this.checkBoxRef.checked());
    //   this.props.deleteTodo(this.props.id);
    }
  };
  render() {
      console.log(this.checkBoxRef.current);
    return (
      <div className="todoitem">
        <input type="checkbox" ref={this.checkBoxRef} />
        <p>{this.props.todoText}</p>
      </div>
    );
  }
}

export default connect(null, { deleteTodo })(Todoitem);
